mapper.select:
select
from sys_log
and date_format(create_date, '%Y-%m-%d') >= #{condition.startDate}
and date_format(create_date, '%Y-%m-%d') <= #{condition.endDate}
and ( log_title like concat('%', #{condition.keyword} , '%') or create_by_name like concat('%', #{condition.keyword} , '%') or request_uri like concat('%', #{condition.keyword} , '%') or remote_addr like concat('%', #{condition.keyword} , '%') or exception_info like concat('%', #{condition.keyword} , '%') or device_name like concat('%', #{condition.keyword} , '%') )
and log_type in ('login','logout')
and log_type in ('insert','update','delete','select','access')
and log_type = 'error'
and tenant_id = #{condition.tenantId}
order by ${condition.orderby}
order by create_date desc
