mapper.update:
update sys_role
role_name = #{roleName,jdbcType=VARCHAR}, role_code = #{roleCode,jdbcType=VARCHAR}, role_weight = #{roleWeight,jdbcType=NUMERIC}, role_type = #{roleType,jdbcType=VARCHAR}, user_type = #{userType,jdbcType=VARCHAR}, is_sys = #{isSys,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, update_by = #{updateBy,jdbcType=VARCHAR}, update_date = #{updateDate,jdbcType=TIMESTAMP}, comments = #{comments,jdbcType=VARCHAR}, tenant_id = #{tenantId,jdbcType=VARCHAR},
id = #{id}

update sys_role


        role_name = #{update.roleName,jdbcType=VARCHAR},


