mapper.select:
select u.id, u.username, u.name, u.short_name, u.dept_id, u.emp_code, u.emp_posts, u.emp_status, u.wubi_code, u.identity_no, u.native_place, u.sex, u.birthday, u.avatar, u.email, u.mobile, u.telephone, u.user_type, u.user_weight, u.mgr_type, u.first_visit_date, u.last_visit_ip, u.last_visit_date, u.allow_start_time, u.allow_end_time, u.status, u.create_date, u.update_date, u.comments, u.tenant_id, o.office_name, d.dept_name from sys_user u inner join sys_office o on o.id = u.tenant_id and o.status = 1 left join sys_dept d on d.id = u.dept_id and d.status = 1 where 1 = 1 and u.status != 0
and u.username like concat('%', #{condition.username} , '%')
and u.name like concat('%', #{condition.name} , '%')
and d.ancestor_id like concat('%',#{condition.deptId}, '%')
and u.emp_code = #{condition.empCode}
and u.short_name like concat('%', #{condition.shortName} , '%')
and u.wubi_code = #{condition.wubiCode}
and u.sex = #{condition.sex}
and u.user_type = #{condition.userType}
and u.mgr_type = #{condition.mgrType}
and u.status = #{condition.status}
and u.tenant_id = #{condition.tenantId}
and u.id not in(select r.user_id from sys_user_role r where r.role_id = #{condition.roleId} and r.tenant_id = #{condition.tenantId})
order by ${condition.orderby}
order by u.dept_id, u.user_weight desc

select
    u.id, u.username, u.name, u.short_name,
    u.dept_id, u.emp_code, u.emp_posts, u.emp_status,
    u.wubi_code, u.identity_no, u.native_place, u.sex, u.birthday, u.avatar,
    u.email, u.mobile, u.telephone, u.user_type, u.user_weight, u.mgr_type,
    u.first_visit_date, u.last_visit_ip, u.last_visit_date, u.allow_start_time, u.allow_end_time,
    u.status, u.create_date, u.update_date, u.comments, u.tenant_id,
    o.office_name, d.dept_name
from sys_user u
inner join sys_office o on o.id = u.tenant_id and o.status = 1
inner join sys_user_role r on u.id = r.user_id
    and r.role_id = #{condition.roleId}
    and r.tenant_id = #{condition.tenantId}
left join sys_dept d on d.id = u.dept_id and d.status = 1
where 1 = 1
    and u.status != 0

    and u.username like concat('%', #{condition.username} , '%')


    and u.name like concat('%', #{condition.name} , '%')


    and d.ancestor_id like concat('%',#{condition.deptId}, '%')


    and u.emp_code = #{condition.empCode}


    and u.short_name like concat('%', #{condition.shortName} , '%')


    and u.wubi_code = #{condition.wubiCode}


    and u.sex = #{condition.sex}


    and u.user_type = #{condition.userType}


    and u.mgr_type = #{condition.mgrType}


    and u.status = #{condition.status}



        order by ${condition.orderby}


        order by u.dept_id, u.user_weight desc
