name = #{name,jdbcType=VARCHAR}, age = #{age,jdbcType=INTEGER}, short_name = #{shortName,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, create_by = #{createBy,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_by = #{updateBy,jdbcType=VARCHAR}, update_date = #{updateDate,jdbcType=TIMESTAMP}, comments = #{comments,jdbcType=VARCHAR},
where id = #{id}

